      int httpResponseCode = http.POST("{\"name\":\"sensor\",\"value\":\"123\"}");

      Serial.print("HTTP Response code: ");
      Serial.println(httpResponseCode);

      // Free resources
      http.end();
    }
    else {
      Serial.println("WiFi Disconnected");
    }
